use super::color_f32_to_u8;
use crate::{ParseColorError, Srgb};
use regex::Regex;
use std::str::FromStr;

fn computed_value(srgb: Srgb) -> Srgb {
    Srgb::new(
        color_f32_to_u8(srgb.red) as f32 / 255.,
        color_f32_to_u8(srgb.green) as f32 / 255.,
        color_f32_to_u8(srgb.blue) as f32 / 255.,
        srgb.alpha,
    )
}

fn color_from_css(css: &str) -> Result<Srgb, ParseColorError> {
    thread_local! {
        static COMMENT: Regex = Regex::new(r"/\*.*?\*/").unwrap();
    }
    COMMENT.with(|comment| Srgb::from_str(&comment.replace_all(css, " ")))
}

macro_rules! test_computed_value {
    ($property:expr, $specified:expr) => {
        assert!(color_from_css($specified).is_ok());
    };
    ($property:expr, $specified:expr, $computed:expr) => {
        assert_eq!(
            computed_value(color_from_css($specified).unwrap()),
            computed_value(Srgb::from_str($computed).unwrap()),
        );
    };
    ($property:expr, $specified:expr, $computed:expr, $title:expr) => {
        assert_eq!(
            computed_value(color_from_css($specified).unwrap()),
            computed_value(Srgb::from_str($computed).unwrap()),
            "{}",
            $title,
        );
    };
}

macro_rules! test_invalid_value {
    ($property:expr, $value:expr) => {
        assert!(color_from_css($value).is_err());
    };
    ($property:expr, $value:expr, $title:expr) => {
        assert!(color_from_css($value).is_err(), "{}", $title);
    };
}

macro_rules! test_valid_value {
    ($property:expr, $value:expr) => {
        assert!(color_from_css($value).is_ok());
    };
    ($property:expr, $value:expr, $expected:expr) => {
        assert_eq!(
            computed_value(color_from_css($value).unwrap()),
            Srgb::from_str($expected).unwrap(),
        );
    };
}

// CSS Color Level 3: getComputedStyle().color
// https://github.com/web-platform-tests/wpt/blob/master/css/css-color/parsing/color-computed.html
#[test]
#[rustfmt::skip]
fn color_computed() {
    //test_computed_value!("color", "currentcolor", "rgb(255, 0, 0)");
    test_computed_value!("color", "transparent", "rgba(0, 0, 0, 0)");
    test_computed_value!("color", "red", "rgb(255, 0, 0)");
    test_computed_value!("color", "magenta", "rgb(255, 0, 255)");
    test_computed_value!("color", "#234", "rgb(34, 51, 68)");
    test_computed_value!("color", "#FEDCBA", "rgb(254, 220, 186)");
    test_computed_value!("color", "rgb(2, 3, 4)");
    test_computed_value!("color", "rgb(100%, 0%, 0%)", "rgb(255, 0, 0)");
    test_computed_value!("color", "rgba(2, 3, 4, 0.5)");
    test_computed_value!("color", "rgba(2, 3, 4, 50%)", "rgba(2, 3, 4, 0.5)");
    test_computed_value!("color", "hsl(120, 100%, 50%)", "rgb(0, 255, 0)");
    test_computed_value!("color", "hsla(120, 100%, 50%, 0.25)", "rgba(0, 255, 0, 0.25)");
    test_computed_value!("color", "rgb(-2, 3, 4)", "rgb(0, 3, 4)");
    test_computed_value!("color", "rgb(100, 200, 300)", "rgb(100, 200, 255)");
    test_computed_value!("color", "rgb(20, 10, 0, -10)", "rgba(20, 10, 0, 0)");
    test_computed_value!("color", "rgb(100%, 200%, 300%)", "rgb(255, 255, 255)");
}

// CSS Color Level 4: Computation of colors using hex color notation
// https://github.com/web-platform-tests/wpt/blob/master/css/css-color/parsing/color-computed-hex-color.html
#[test]
fn color_computed_hex_color() {
    #[rustfmt::skip]
    let tests = [
        ["#fff", "rgb(255, 255, 255)", "Valid 3-digit hex"],
        ["#ffff", "rgb(255, 255, 255)", "Valid 4-digit hex"],
        ["#ffffff", "rgb(255, 255, 255)", "Valid 6-digit hex"],
        ["#ffffffff", "rgb(255, 255, 255)", "Valid 8-digit hex"],
        ["#FFCc99", "rgb(255, 204, 153)", "Valid 6-digit hex"],
        ["#369", "rgb(51, 102, 153)", "Valid 3-digit hex"],
    ];
    for test in tests {
        test_computed_value!("color", test[0], test[1], test[2]);
    }
}

